// import logo from './logo.svg';
import React, { useEffect, useState } from 'react';
import './App.css';
import functions from './services/functions';
import "./styles/index.scss";
import { Row, Col, Container } from 'reactstrap';
import Ripple from 'react-ripples';
function App() {

  const [usersData, setUsersData] = useState([]);
  const [personSelected, setPersonSelected] = useState('');
  const [startSpin, setStartSpin] = useState(false);
  const [startSpinBack, setStartSpinBack] = useState(false);
  const [showBackSide, setBackSide] = useState(false);
  const [showFrontSide, setFrontSide] = useState(false);
  useEffect(() => {
    const getData = async () => {
      const { getUsers } = functions;
      setUsersData(await getUsers());
    }
    getData();
    // postDebt(1, 'Carro', '199.50');
  }, []);

  const setPerson = (person) => {
    setPersonSelected(person);
  }
  console.log(usersData);

  // const viewBackSide = () => {
  //   if (!showBackSide) {
  //     setStartSpin(true);
  //     setTimeout(() => {
  //       setBackSide(true);
  //       setFrontSide(!showFrontSide);
  //       setStartSpin(false);
  //       // setStartSpin(!startSpin);
  //     }, 600);
  //   } else {
  //     setStartSpinBack(true);
  //     setTimeout(() => {
  //       setFrontSide(true);
  //       setBackSide(false);
  //       setStartSpinBack(false);
  //       // setStartSpin(!startSpin);
  //     }, 700);
  //   }
  // }
  const showData = () => {
    if (!showBackSide) {
      setStartSpinBack(true);
      setTimeout(() => {
        setFrontSide(true);
        setBackSide(false);
        setStartSpinBack(false);
        // setStartSpin(!startSpin);
      }, 700);
    }
  }
  const editData = () => {
    if (showBackSide) {
      setStartSpin(true);
      setTimeout(() => {
        setBackSide(true);
        setFrontSide(!showFrontSide);
        setStartSpin(false);
        // setStartSpin(!startSpin);
      }, 600);
    }
  }
  return (
    <div className="main-container">
      <Container>
        <Row id="main-row" className="d-flex justify-content center">
          <Col id="names-column" sm="12" md="4">
            <div id="container-names">
              <h1 className="title">Nomes</h1>
              {usersData.map((user, index) => (
                <div id="container-save-view">
                  <li className="names" key={index}>{user.name}</li>
                  <div id="container-buttons">
                    <Ripple onClick={() => editData()} className="buttons">Editar</Ripple>
                    <Ripple className="buttons" onClick={() => showData()}>Visualizar</Ripple>
                  </div>
                </div>
              ))}
            </div>
          </Col>
          <Col id="data-column" sm="12" md="8">
            {
              showFrontSide &&
              <div className={startSpin ? "spinning" : ""} id="container-data">
                <h1 className="title">Dados</h1>
                <div id="data-box">
                  <div id="data-name">
                    <h3>Nome</h3>
                    <input value={personSelected.name} id="name-field" placeholder="Igor Henrique"></input>
                  </div>
                  <div id="data-description">
                    <h3>Descrição</h3>
                    <input id="name-field" placeholder="Igor Henrique"></input></div>
                  <div id="data-value">
                    <h3>Valor</h3>
                    <input id="name-field" placeholder="Igor Henrique"></input>
                  </div>
                  <div id="data-button">
                    <Ripple id="button">Salvar</Ripple>
                  </div>
                </div>
              </div>
            }
            {
              showBackSide &&
              <div className={startSpinBack ? "spinning-back" : ""} id="container-data">
                <h1 className="title">Oitenta centavos</h1>
                <div id="data-box">
                  <div id="data-name">
                    <h3>Nome</h3>
                    <input value={personSelected.name} id="name-field" placeholder="Igor Henrique"></input>
                  </div>
                  <div id="data-description">
                    <h3>Descrição</h3>
                    <input id="name-field" placeholder="Igor Henrique"></input></div>
                  <div id="data-value">
                    <h3>Valor</h3>
                    <input id="name-field" placeholder="Igor Henrique"></input>
                  </div>
                  <div id="data-button">
                    <Ripple id="button">Salvar</Ripple>
                  </div>
                </div>
              </div>
            }
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
