import instances from "./api";
const { instanceUsers, debts } = instances;
const uuid = '08192f80-ec50-49d3-abd2-8623a618f85a';
const functions = {
  getUsers: async () => {
    const users = await instanceUsers.get('/');
    return users.data;
  },
  postDebt: async (id, motivo, valor) => {
    const body = {
      idUsuario: id,
      motivo: motivo,
      valor: valor
    };

    debts.post(`/divida?uuid=${uuid}`, body).then((res) => {
      console.log(res);
    })
  }
}

export default functions;
