import instances from "./api";
const { instanceUsers, debts } = instances;
const functions = {
  getUsers: async () => {
    const users = await instanceUsers.get('/users');
    console.log(users);
  },
  postDebt: async (id, motivo, valor) => {
    const body = {
      idUsuario: id,
      motivo: motivo,
      valor: valor
    };

    debts.post(`/divida/${id}`, body).then((res) => {
      console.log(res);
    })
  }
}

export default functions;
