              <div id="container-data">
                <h1 className="title">Dados</h1>
                <div id="data-box">
                  <div id="data-name">
                    <h3>Nome</h3>
                    <input value={personSelected.name} id="name-field" placeholder="Igor Henrique"></input>
                  </div>
                  <div id="data-description">
                    <h3>Descrição</h3>
                    <input id="name-field" placeholder="Igor Henrique"></input></div>
                  <div id="data-value">
                    <h3>Dívida</h3>
                    <input id="name-field" placeholder="Igor Henrique"></input>
                  </div>
                  <div id="data-button">
                    <Ripple id="button">Salvar</Ripple>
                  </div>
                </div>
              </div>
            }
            {
              cardRendered === 'Visualizar' &&
              <div id="container-data">
                <h1 className="title">Oitenta centavos</h1>
                <div id="data-box">
                  <div id="data-name">
                    <h3>Nome</h3>
                    <input value={personSelected.name} id="name-field" placeholder="Igor Henrique"></input>
                  </div>
                  <div id="data-description">
                    <h3>Descrição</h3>
                    <input id="name-field" placeholder="Igor Henrique"></input></div>
                  <div id="data-value">
                    <h3>Valor</h3>
                    <input id="name-field" placeholder="Igor Henrique"></input>
                  </div>
                  <div id="data-button">
                    <Ripple id="button">Salvar</Ripple>
                  </div>
                </div>
              </div>
            }
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
