import instances from "./api";
const { instanceUsers, debts } = instances;
const functions = {
  getUsers: async () => {
    const users = await instanceUsers.get('/account');
    return users.data;
  },
  postDebt: async (id, motivo, valor) => {
    const body = {
      name: id,
      balance: motivo,
      description: valor
    };

    debts.put('/account', body).then((res) => {
      console.log(res);
    })
  }
}

export default functions;
