// import logo from './logo.svg';
import React, { useEffect, useState } from 'react';
import './App.css';
import functions from './services/functions';
import "./styles/index.scss";
import { Row, Col, Container } from 'reactstrap';
import Ripple from 'react-ripples';
import NumberFormat from 'react-number-format';

function App() {

  const [usersData, setUsersData] = useState([]);
  const [cardRendered, setRender] = useState('PadrÃ£o');
  const [personSelected, setPersonSelected] = useState('');
  const [buttonActive, setButtonActive] = useState(false);
  const [name, setName] = useState(null);
  const [balance, setBalance] = useState(null);
  const [description, setDescription] = useState(null);
  const [personObject, setPersonObject] = useState({ name: null, balance: null, description: null });

  useEffect(() => {
    const getData = async () => {
      const { getUsers } = functions;
      const data = await getUsers();
      setUsersData(data.accounts);
      // setRender('Visualizar');
    }
    getData();

  }, []);

  const sendData = async () => {
    const { putDebts } = functions;
    putDebts(personObject)
  }
  const renderView = (card, position) => {
    setPersonSelected(usersData[position]);
    setPersonObject({ name: null, balance: null, description: null });
    const buttonsToRemove = document.getElementsByClassName("buttons-edit");
    const buttons = document.getElementsByClassName("buttons-view");
    const arrButtons = Array.from(buttons);
    const arrButtonsToRemove = Array.from(buttonsToRemove);
    arrButtonsToRemove.forEach(function (button) {
      if (button.classList.contains("active")) {
        button.classList.remove("active");
        button.classList.add("inactive");
      }
    });
    console.log(arrButtons[position]);
    arrButtons.forEach(function (button) {
      if (button.classList.contains("active")) {
        button.classList.remove("active");
        button.classList.add("inactive");
      }
    });
    arrButtons[position].classList.remove("inactive");
    arrButtons[position].classList.add("active");
    setRender(card);
    setButtonActive(true);
  }

  const renderEdit = (card, position) => {
    setPersonSelected(usersData[position]);
    setPersonObject({ name: null, balance: null, description: null });
    const buttonsToRemove = document.getElementsByClassName("buttons-view");
    const buttons = document.getElementsByClassName("buttons-edit");
    const arrButtons = Array.from(buttons);

    const arrButtonsToRemove = Array.from(buttonsToRemove);
    arrButtonsToRemove.forEach(function (button) {
      if (button.classList.contains("active")) {
        button.classList.remove("active");
        button.classList.add("inactive");
      }
    });

    arrButtons.forEach(function (button) {
      if (button.classList.contains("active")) {
        button.classList.remove("active");
        button.classList.add("inactive");
      }
    });
    arrButtons[position].classList.remove("inactive");
    arrButtons[position].classList.add("active");
    console.log(arrButtons[position]);
    setRender(card);
    setButtonActive(true);
  }

  const handlePerson = ({ currentTarget: { name, value } }) => {
    setPersonObject({ ...personObject, [name]: value });
    console.log(personObject);
  }

  return (
    <div className="main-container">
      <Container>
        <Row id="main-row" className="d-flex justify-content center">
          <Col id="names-column" sm="12" md="4">
            <div id="container-names">
              <h1 className="title">Nomes</h1>
              {usersData.map((user, index) => (
                <div id="container-save-view">
                  <li className="names" key={index}>{user.name}</li>
                  <div id="container-buttons">
                    <Ripple id={`button-edit-${index}`} onClick={() => renderEdit('Editar', index)} className="buttons-edit inactive">Editar</Ripple>
                    <Ripple id={`button-view-${index}`} className="buttons-view inactive" onClick={() => renderView('Visualizar', index)}>Visualizar</Ripple>
                  </div>
                </div>
              ))}
            </div>
          </Col>
          <Col id="data-column" sm="12" md="8">
            {
              cardRendered === 'Editar' &&
              <div id="container-data">
                <h1 className="title">Editar dados</h1>
                <div id="data-box">
                  <div id="data-name">
                    <h3>Nome</h3>
                    <input
                      name="name"
                      onChange={handlePerson}
                      value={personObject.name === null ? personSelected.name : personObject.name}
                      id="name-field"
                      placeholder="Igor Henrique"
                    />
                  </div>
                  <div id="data-description">
                    <h3>DescriÃ§Ã£o</h3>
                    <input
                      name="description"
                      onChange={handlePerson}
                      value={personObject.description === null ? personSelected.description : personObject.description}
                      id="name-field"
                      placeholder="Igor Henrique"
                    />
                  </div>
                  <div id="data-value">
                    <h3>DÃ­vida</h3>
                    <NumberFormat
                      name="balance"
                      value={personObject.balance === null ? personSelected.balance : personObject.balance}
                      onChange={handlePerson}
                      id="name-field"
                      thousandSeparator={true}
                      prefix={'$'}
                    />
                  </div>
                  <div onClick={sendData} id="data-button">
                    <Ripple id="button">Salvar</Ripple>
                  </div>
                </div>
              </div>
            }
            {
              cardRendered === 'Visualizar' &&
              <div id="container-data">
                <h1 className="title">Visualizar dados</h1>
                <div id="data-box">
                  <div id="data-name">
                    <h3>Nome</h3>
                    <div className="information-container">
                      <span>{personSelected.name}</span>
                    </div>
                  </div>
                  <div id="data-description">
                    <h3>DescriÃ§Ã£o</h3>
                    <div className="information-container">
                      <span>{personSelected.description}</span>
                    </div>
                  </div>
                  <div id="data-value-formatted">
                    <h3>Valor</h3>
                    <div className="information-container">
                      <NumberFormat
                        id="name-field"
                        value={personSelected.balance}
                        displayType={'text'}
                        thousandSeparator={true}
                        prefix={'$'}
                      />
                      {/* <span>{personSelected.balance}</span> */}
                    </div>
                  </div>
                </div>
              </div>
            }
            {
              cardRendered === 'PadrÃ£o' &&
              <div id="description-container">
                <h1 className="title">Tela padrÃ£o</h1>
                <div id="description-text-container">
                  <span id="description-text">Neste sistema vocÃª pode visualiar, editar e modificar o cadastro de qualquer cliente</span>
                </div>
              </div>
            }
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
